def decodificar_caesar(cadena_cifrada, veces):
    mensaje_original = ""

    for letra in cadena_cifrada:
        if letra.isalpha():
            base_ascii = ord('a') if letra.islower() else ord('A')
            nuevo_ascii = (ord(letra) - base_ascii - veces) % 26 + base_ascii
            mensaje_original += chr(nuevo_ascii)
        else:
            mensaje_original += letra

    return mensaje_original

mensaje_cifrado = input("Ingresa el mensaje cifrado: ")
veces = int(input("Cu√°ntas veces se movieron las letras para cifrarlo: "))

mensaje_decodificado = decodificar_caesar(mensaje_cifrado, veces)
print("Mensaje original:", mensaje_decodificado)
